mapper.select:
select
u.id,u.user_name,u.true_name,u.password,u.create_time,u.default_lang_id,u.create_user_id,
group_concat(u_g.`group_name` order by u_g.`group_name` asc) as show_groups
,u_l.user_language as default_lang
from user u
LEFT JOIN user_language u_l on u_l.id = u.default_lang_id
left join authority_user_group a_u_g on u.`id` = a_u_g.`user_id`
left join user_group u_g on u_g.`id` = a_u_g.`group_id`

where 1=1



        AND u.id > #{id}
        AND u.id >= #{id}
        AND u.id = #{id}
        AND u.id < #{id}
        AND u.id <= #{id}

            /*默认应该是=*/
         AND   u.id = #{id}





          AND u.user_name > #{userName}
        AND u.user_name > #{userName}
        AND u.user_name = #{userName}
        AND u.user_name < #{userName}
        AND u.user_name <= #{userName}
        AND u.user_name like '%${userName}%'
        AND u.user_name not like '%${userName}%'

            /*默认应该是=*/
            AND u.user_name = #{userName}







          AND u.true_name > #{trueName}
        AND u.true_name > #{trueName}
        AND u.true_name = #{trueName}
        AND u.true_name < #{trueName}
        AND u.true_name <= #{trueName}
        AND u.true_name like '%${trueName}%'
        AND u.true_name not like '%${trueName}%'

            /*默认应该是=*/
            AND u.true_name = #{trueName}






          AND u.password > #{password}
        AND u.password > #{password}
        AND u.password = #{password}
        AND u.password < #{password}
        AND u.password <= #{password}
        AND u.password like '%${password}%'
        AND u.password not like '%${password}%'

            /*默认应该是=*/
            AND u.password = #{password}






          AND create_time > #{createTime}
        AND create_time > #{createTime}
        AND create_time = #{createTime}
        AND create_time < #{createTime}
        AND create_time <= #{createTime}
        AND create_time like '%${createTime}%'
        AND create_time not like '%${createTime}%'

            /*默认应该是=*/
            AND create_time = #{createTime}





    AND u.default_lang_id IN
    (

        #{item}

    )




    AND a_u_g.group_id IN
    (

        #{item}

    )





          AND u.create_user_id > #{createUserId}
        AND u.create_user_id > #{createUserId}
        AND u.create_user_id = #{createUserId}
        AND u.create_user_id < #{createUserId}
        AND u.create_user_id <= #{createUserId}
        AND u.create_user_id like '%${createUserId}%'
        AND u.create_user_id not like '%${createUserId}%'

            /*默认应该是=*/
            AND u.create_user_id = #{createUserId}







    AND u.id  IN
    (

        #{item}

    )

group by u.`id`

ORDER BY



     u.id
     u.user_uame
     u.true_name
    ,u.password
     u.create_time
    u_l.user_language
     group_concat(u_g.`group_name`  u_g.`group_name` asc) 

     DESC , 
     ASC ,




     u.id
     u.user_uame
     u.true_name
    ,u.password
     u.create_time
    u_l.user_language
     group_concat(u_g.`group_name`  u_g.`group_name` asc) 

     DESC , 
     ASC ,

1
/*ORDER BY u.user_name ASC*/

mapper.update:
UPDATE
user u SET
u.id = #{id}
,u.user_name = #{userName}
,u.true_name = #{trueName}
,u.password = #{password}
,u.create_time = #{createTime}
,u.default_lang_id = #{defaultLangId}
,u.u.create_user_id = #{createUserId}
,u.show_groups = #{showGroups}
WHERE
u.id = #{id}

UPDATE
user u SET
u.show_groups = CASE u.id

  WHEN #{item.id} THEN #{item.showGroups}

END
WHERE   u.id IN
(

    #{item.id}

)
